package com.example.arnold.remindme;

public class RemindmeItem {

    String hymn;
    String title;
    String hymnum;

    public RemindmeItem(String hymn) {
        // TODO Auto-generated constructor stub
        this.hymnum = hymnum;
        this.hymn = hymn;
        this.title = title;
    }

    public String getTitle() {
        return title;
    }

    public void setTitle(String title) {
        this.title = title;
    }

    public String getHymnum() {
        return hymnum;
    }

    public void setHymnum(String hymnum) {
        this.hymnum = hymnum;
    }

    public String getHymn() {
        return hymn;
    }

    public void setHymn(String hymn) {
        this.hymn = hymn;
    }



}
